import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme } from './Theme';
import { GlobalStyles } from './Global';


function App() {
  // sets theme to state
  const [theme, setTheme] = useState('light');

  // toggles between themes
  const toggleTheme = () => {
    // If the theme is light, switch to dark
    if (theme === 'light') {
      setTheme('dark');
      // else switch to light
    } else {
      setTheme('light');
    }
  }

  const StyledButton = styled.button`
  /* Adapt the colors based on primary prop */
  background: '#E2E2E2';
  color: '#363537';

  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid '#E2E2E2';
  border-radius: 25px;
  `;
  return (
    // ternary that allows us to toggle between the two themes we've set
    <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
      <>
        <GlobalStyles />
        <h1>It's a {theme} theme!</h1>
        <StyledButton onClick={toggleTheme}>Toggle theme</StyledButton>
        <footer>
        </footer>
      </>
    </ThemeProvider>
  );
}

export default App;
