import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme } from './Theme';
import { GlobalStyles } from './Global';

const StyledButton = styled.button`
/* Adapt the colors based on primary prop */
background: '#E2E2E2';
color: '#363537';

font-size: 1em;
margin: 1em;
padding: 0.25em 1em;
border: 2px solid '#E2E2E2';
border-radius: 25px;
`;

const CenteredDiv = styled.div`
display: flex;
justify-content: center;
`;

const StickyFooter = styled.footer`
position: absolute;
bottom: 0;
width: 100%;
height: 2.5rem;
`;

function App() {
  // sets theme to state
  const [theme, setTheme] = useState('light');

  // toggles between themes
  const toggleTheme = () => {
    // If the theme is light, switch to dark
    if (theme === 'light') {
      setTheme('dark');
      // else switch to light
    } else {
      setTheme('light');
    }
  }

  return (
    // ternary that allows us to toggle between the two themes we've set
    <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
      <>
        <GlobalStyles />
        <h1>It's a {theme} theme! Click the button to switch!</h1>
        <CenteredDiv>
          <StyledButton onClick={toggleTheme}>Click me!</StyledButton>
        </CenteredDiv>
        <footer>
          <p>Tinkered with by Miguel Palo</p>
        </footer>
      </>
    </ThemeProvider>
  );
}

export default App;
